const { taxRates, search, price, valueAdditions } = el.value;
ctx.checkType(taxRates, 'array', 'taxRates');
ctx.checkType(price, 'object', 'price');
ctx.checkType(price.value, 'number', 'price.value');
ctx.checkType(price.currencyCode, 'string', 'price.currencyCode');
ctx.checkType(search, 'object', 'search'); 
ctx.checkType(search.checkInDate, 'string', 'search.checkInDate');
ctx.checkType(search.checkOutDate, 'string', 'search.checkOutDate');
ctx.checkType(search.rooms, 'array', 'search.rooms');

const payLater = Array.isArray(valueAdditions) && valueAdditions.includes('pay-later');
const priceBreakdown = await calcPriceBreakdown({ taxRates, price, search, payLater });
return el.clone(priceBreakdown);

async function calcPriceBreakdown({ taxRates, price, search, payLater }) {
    const party = calcParty(search);
    const nights = calcNights(search);
    const taxItems = calcTaxItems(taxRates, price, party, nights, payLater);
    let netRate = price.value;
    for (const tax of taxItems) {
        const included = !tax.paidAtHotel && !tax.excludedFromTotals;
        if (included) {
            netRate -= tax.price.value;
        }
    }
    return [
        {
            type: 'net-rate',
            price: {
                value: Math.round(netRate),
                currencyCode: price.currencyCode,
            },
            excludedFromTotals: false,
          	paidAtHotel: payLater,
        },
        ...taxItems
    ];
}

function calcTaxItems(taxRates, price, party, nights, payLater) {
    const { adults, children } = party;
    const items = [];
    for (const taxRate of taxRates) {
        const { taxType, excludedFromTotals, paidAtHotel } = taxRate;
        const percentage = Number(taxRate.percentage) || 0;
        const pricePerAdult = ctx.parsePrice(taxRate.pricePerAdult);
        const pricePerChild = ctx.parsePrice(taxRate.pricePerChild);
        // By default currency code is the same as in price,
        // but can be overridden by fixed price per person
        let currencyCode = price.currencyCode;
        let value = price.value * percentage / 100;
        if (pricePerAdult && pricePerAdult.value) {
            value += pricePerAdult.value * adults * nights;
            currencyCode = pricePerAdult.currencyCode;
        }
        if (pricePerChild && pricePerChild.value) {
            value += pricePerChild.value * children * nights;
            currencyCode = pricePerChild.currencyCode;
        }
        items.push({
            type: taxType,
            price: {
                value: Math.round(value),
                currencyCode,
            },
            paidAtHotel: excludedFromTotals || paidAtHotel || payLater || false,
            excludedFromTotals,
        });
    }
    return items;
}

function calcParty(search) {
    let adults = 0;
    let persons = 0;
    for (const room of search.rooms) {
        for (const age of room.ages) {
            persons += 1;
            if (age >= 18) {
                adults += 1;
            }
        }
    }
    return { adults, persons, children: persons - adults };
}

function calcNights(search) {
    const from = ctx.moment(search.checkInDate);
    const to = ctx.moment(search.checkOutDate);
    return to.diff(from, 'days');
}

function sumItems(priceBreakdown) {
    return priceBreakdown.reduce((acc, v) => acc + v.price.value, 0);
}
